mapper.select:
SELECT count(*)
FROM profile
WHERE nickname=#{nickname}

mapper.update:
UPDATE profile SET
match_ok = 1
WHERE email=#{email}

mapper.select:
SELECT liker
FROM popularity_list
WHERE email=#{email}

SELECT count(*)
FROM popularity_list
WHERE email=#{email}

SELECT count(*)
FROM popularity_list
WHERE email=#{email} AND liker=#{liker}

SELECT count(*)
FROM my_scrap
WHERE member_email=#{memberEmail} and board_id=#{boardId} and together_id=#{togetherId}
and event_id=#{eventId} and magazine_id=#{magazineId} and title=#{title}

mapper.delete:
DELETE FROM my_scrap 
WHERE member_email=#{memberEmail} and board_id=#{boardId} and together_id=#{togetherId}
and event_id=#{eventId} and magazine_id=#{magazineId} and title=#{title}
